import React from 'react';
import {useContext} from 'react';
import {GlobalContext} from '../Router';



export default function Upcoming() {

    const {upComings, img_url} = useContext(GlobalContext);

    return (
        <div className="movie">
            {upComings.map(upComing => {
                return (<div className="upcoming">
                        <img src={`${img_url}${upComing.poster_path}`} alt=""/>
                        <h1>{upComing.title}</h1>
                        <button className="mb-3">More info</button>
                        </div>

            )})}
                    <nav aria-label="Page navigation example d-center">
                        <ul class="pagination">
                            <li class="page-item">
                            <a class="page-link" href="#" aria-label="Previous">
                                <span aria-hidden="true">&laquo;</span>
                                <span class="sr-only">Previous</span>
                            </a>
                            </li>
                            <li class="page-item"><a class="page-link" href="#">1</a></li>
                            <li class="page-item"><a class="page-link" href="#">2</a></li>
                            <li class="page-item"><a class="page-link" href="#">3</a></li>
                            <li class="page-item">
                            <a class="page-link" href="#" aria-label="Next">
                                <span aria-hidden="true">&raquo;</span>
                                <span class="sr-only">Next</span>
                            </a>
                            </li>
                        </ul>
                    </nav>
        </div>
    )
}
