import React from 'react';
import {useEffect, createContext, useState} from 'react';
import {BrowserRouter, Route, Switch, NavLink} from 'react-router-dom';
import Upcoming from "./Components/Upcoming";

export const GlobalContext = createContext();

export default function Router() {

    const url_upComings = "https://api.themoviedb.org/3/movie/upcoming?api_key=9f71730a503f87329ec0e2ef43411e30";

    const url_details = "https://api.themoviedb.org/3/movie/460465?api_key=9f71730a503f87329ec0e2ef43411e30"

    const img_url = "https://image.tmdb.org/t/p/w500";

    const [upComings, setUpComings] = useState([]);

    const [details, setDetails] = useState({});

    useEffect(() => {
        fetch(url_upComings)
    .then(response => response.json())
    .then(data => setUpComings(data.results));
        }, []);


    return (
        <div>
            <BrowserRouter>
                <NavLink exact to ="/">Estrenos</NavLink>
                <GlobalContext.Provider value={{setUpComings, upComings, img_url}}>
                <Upcoming/>

                </GlobalContext.Provider>

            </BrowserRouter>


        </div>
    )
}
